function lerpColor(a, b, amount) 
    {
        var ah = parseInt(a.replace(/#/g, ''), 16),
        ar = ah >> 16, ag = ah >> 8 & 0xff, ab = ah & 0xff,
        bh = parseInt(b.replace(/#/g, ''), 16),
        br = bh >> 16, bg = bh >> 8 & 0xff, bb = bh & 0xff,
        rr = ar + amount * (br - ar),
        rg = ag + amount * (bg - ag),
        rb = ab + amount * (bb - ab);
        return ("#" + ((1 << 24) + (rr << 16) + (rg << 8) + rb).toString(16).slice(1)).slice(0, 7);
    }

  function getcolor(value) 
  {
    var percentage = parseFloat(value);
    var color;
    if (percentage < 50) {
      amount = percentage / 50;
      color = lerpColor("#00b500", "#e6e600", amount);
    } else {
      amount = (percentage - 50) / 50;
      color = lerpColor("#e6e600", "#ca3838", amount);
    }
    return color;
  }
