function lerpColor(a, b, c, amount) 
{
    if(amount < 0.5)
    {
      amount = amount * 2
      var ah = parseInt(a.replace(/#/g, ''), 16),
      ar = ah >> 16, ag = ah >> 8 & 0xff, ab = ah & 0xff,
      bh = parseInt(b.replace(/#/g, ''), 16),
      br = bh >> 16, bg = bh >> 8 & 0xff, bb = bh & 0xff,
      rr = ar + amount * (br - ar),
      rg = ag + amount * (bg - ag),
      rb = ab + amount * (bb - ab);
    }
    else
    {
      amount = (amount - 0.5) / 0.5
      var bh = parseInt(b.replace(/#/g, ''), 16),
      br = bh >> 16, bg = bh >> 8 & 0xff, bb = bh & 0xff,
      ch = parseInt(c.replace(/#/g, ''), 16),
      cr = ch >> 16, cg = ch >> 8 & 0xff, cb = ch & 0xff,
      rr = br + amount * (cr - br),
      rg = bg + amount * (cg - bg),
      rb = bb + amount * (cb - bb);
    }

    return ("#" + ((1 << 24) + (rr << 16) + (rg << 8) + rb).toString(16).slice(1)).slice(0, 7);
}


