function getBeaufort(metricSpeed)
{
    if(metricSpeed >= 0 && metricSpeed <= 0.2)
    {
        return "wi-wind-beaufort-0";
    }
    else if(metricSpeed >= 0.3 && metricSpeed <= 1.5)
    {
        return "wi-wind-beaufort-1";
    }
    else if(metricSpeed >= 1.6 && metricSpeed <= 3.3)
    {
        return "wi-wind-beaufort-2";
    }
    else if(metricSpeed >= 3.4 && metricSpeed <= 5.4)
    {
        return "wi-wind-beaufort-3";
    }
    else if(metricSpeed >= 5.5 && metricSpeed <= 7.9)
    {
        return "wi-wind-beaufort-4";
    }
    else if(metricSpeed >= 8.0 && metricSpeed <= 10.7)
    {
        return "wi-wind-beaufort-5";
    }
    else if(metricSpeed >= 10.8 && metricSpeed <= 13.8)
    {
        return "wi-wind-beaufort-6";
    }
    else if(metricSpeed >= 13.9 && metricSpeed <= 17.1)
    {
        return "wi-wind-beaufort-7";
    }
    else if(metricSpeed >= 17.2 && metricSpeed <= 20.7)
    {
        return "wi-wind-beaufort-8";
    }
    else if(metricSpeed >= 20.8 && metricSpeed <= 24.4)
    {
        return "wi-wind-beaufort-9";
    }
    else if(metricSpeed >= 24.5 && metricSpeed <= 28.4)
    {
        return "wi-wind-beaufort-10";
    }
    else if(metricSpeed >= 28.5 && metricSpeed <= 32.6)
    {
        return "wi-wind-beaufort-11";
    }
    else if(metricSpeed >= 32.7)
    {
        return "wi-wind-beaufort-12";
    }

}

function getPressureGauge(current_hPa)
{
    const min = 970;
    const max = 1050;
    const gaugeMin = 0;
    const gaugeMax = 180;
    const delta_p = max - min;
    const offset = 10;

    pos = (current_hPa - min) / delta_p;
    return (pos * (gaugeMax - gaugeMin) + gaugeMin) + offset;
}

function getSunPos(west, south, east)
{
    position = (((-west) + east) * (1 / south) * 40);
    if(position > 81)
    {
        position = 81;
    }
    else if(position < -81)
    {
        position = -81;
    }
    return position;
}

function getWindDirChar(angle)
{
    angle = angle + 22.5;
    if(angle >= 0 && angle < 45)
    {
        return "Z";
    }
    else if(angle >= 45 && angle < 90)
    {
        return "ZW";
    }
    else if(angle >= 90 && angle < 135)
    {
        return "W";
    }
    else if(angle >= 135 && angle < 180)
    {
        return "NW";
    }
    else if(angle >= 180 && angle < 225)
    {
        return "N";
    }
    else if(angle >= 225 && angle < 270)
    {
        return "NO";
    }
    else if(angle >= 270 && angle < 315)
    {
        return "O";
    }
    else if(angle >= 315 && angle < 360)
    {
        return "ZO";
    }
}

